    private InputStream inputStream;
    private OutputStream outputStream;


    //这个就是需要被实例化的类
    private volatile static SocketUtil socketUtil = null;
    private String Res;

    //这里为全局提供访问的节点
    public static SocketUtil getInstance() {
        //涉及延时加载，当需要这个类的时候才会被实例化
        if (socketUtil == null) {
            synchronized (SocketUtil.class) {
                if (socketUtil == null) {
                    socketUtil = new SocketUtil();
                }
            }
        }
        return socketUtil;
    }

    /**
     * =============================================================================================
     * 请求TCP socket
     *
     * @param IP   IP地址
     * @param port 端口号
     * @param str  字符消息
     * @return
     * @throws IOException IO异常
     */
    public String SocketRequest(final String IP, final int port, final String str) throws IOException {


        socket = new Socket(IP, port);

        inputStream = socket.getInputStream();
        outputStream = socket.getOutputStream();

        outputStream.write(str.getBytes());
        outputStream.flush();

        byte[] buffer = new byte[1024];
        int bytes;
        //读取数据
        bytes = inputStream.read(buffer);
        if (bytes > 0) {
            final byte[] data = new byte[bytes];
            System.arraycopy(buffer, 0, data, 0, bytes);

            Res = new String(data);

            Log.i("SocketUtil  ", "接收到的数据为---->：" + new String(data));

            System.out.println("接收到的数据为---->：" + new String(data));

            inputStream.close();
            outputStream.close();
            socket.close();
        }
        return Res;

    }


